var test      = require('blue-tape');
var getCoords = require('./build/index');

test("get right coords", function(t) {
  t.plan(2);

  return getCoords('NYC')
    .then(function(result) {
      t.equal(result.lat,'40.782');
      t.equal(result.lng, '-73.832');
    });
});

test("error on bad query", function(t) {
  return getCoords('i am a bad query')
    .catch(function(e) {
      t.equal(e.message, 'Bad query');
    });
});
